let attackerSize = game.wfrp4e.config.actorSizeNums[args.attacker.details.size.value];
if (attackerSize > actorSize) {
   args.actor.addCondition("prone");
   ChatMessage.create({content: `<b>Atak Ogonem</b>: ${args.actor.prototypeToken.name} otrzymuje Stan @Condition[Powalenie]`, speaker : {alias: args.attacker.prototypeToken.name}});
}

