YQKv7HWnnxmcOQty.script:
let chatData = {content: "", speaker : {alias : this.effect.name}, whisper: ChatMessage.getWhisperRecipients("GM")}
let wounds = duplicate(this.actor.status.wounds);
let regen = (await new Roll("1d10").roll()).total;
if (wounds.value < wounds.max) {
   if (wounds.value > 0) {
      wounds.value += regen;
      if (wounds.value > wounds.max) {
         wounds.value = wounds.max;
      }
      chatData.content += `<b>${this.actor.name}</b> Odzyskana żywotność:  ${regen} .`;
      if (regen == 10) {
         chatData.content += `<br>Dodatkowo, zregenerowano Ranę Krytyczną.`;
      }
   }
   else if (regen >= 8) {
      chatData.content += `<b>${this.actor.name}</b> wyrzucił ${regen} i odzyskał 1 punkt żywotności.`
      wounds.value += 1;
      if (regen == 10) {
         chatData.content += `<br>Dodatkowo, zregenerowano Ranę Krytyczną.`
      }
   }
   else {
      chatData.content += `<b>${this.actor.name}</b> Rzut na regenerację: ${regen} - brak efektu.`
   }
   this.actor.update({"system.status.wounds" : wounds})
   ChatMessage.create(chatData)
}

