{
    let choice = await Dialog.wait({
        title: this.effect.name,
        content: `<p>Zniwelować obrażenia dzięki <strong>${this.effect.name}</strong>?`,
        buttons: {
            yes: {
                label: "Yes",
                callback: () => {
                    return true;
                }
            },
            no: {
                label: "No",
                callback: () => {
                    return false;
                }
            }
        }
    })

