scriptData.script:
let caster = this.effect.sourceActor

if (caster)
{
    let bonus = caster.system.characteristics.wp.bonus
    this.actor.modifyWounds(bonus)

    this.script.scriptMessage(`<strong>${this.actor.prototypeToken.name}</strong> Odzyskuje Rany: ${bonus}`)
}

let test = await this.actor.setupSkill(game.i18n.localize("NAME.Cool"), {appendTitle : ` - ${this.effect.name}`})
await test.roll();

// Kind of insane but whatever
let opposedResult = test.opposedMessages[0]?.getOppose()?.resultMessage?.getOpposedTest()?.result

if (opposedResult?.winner == "attacker")
{
    if (opposedResult.differenceSL < 6)
    {
        this.actor.addCondition("fatigued", Math.floor(opposedResult.differenceSL / 2))
    }
    else if (opposedResult.differenceSL >= 6)
    {
        this.actor.addCondition("unconscious");
    }
}

