dWfpyQTb5zFPfOv2.script:
let caster = await fromUuid(this.effect.origin);
if (this.actor.isOwner) {
    if (!this.actor.has(game.i18n.localize("NAME.Daemonic")) && !this.actor.has(game.i18n.localize("NAME.Undead"))) {
        let bleeding = this.actor.hasCondition("bleeding");
        let fatigued = this.actor.hasCondition("fatigued");
        if (bleeding) await this.actor.removeCondition("bleeding", bleeding.flags.wfrp4e.value);
        if (fatigued) await this.actor.removeCondition("fatigued", fatigued.flags.wfrp4e.value);
    } else if (this.actor.has(game.i18n.localize("NAME.Undead"))) {
        await this.actor.applyBasicDamage(caster.characteristics.wp.bonus, {damageType : game.wfrp4e.config.DAMAGE_TYPE.IGNORE_ALL});
    }
}

