effects.script:
let applyAP = (args.damageType == game.wfrp4e.config.DAMAGE_TYPE.IGNORE_TB || args.damageType == game.wfrp4e.config.DAMAGE_TYPE.NORMAL);
let AP = args.AP;
let metalValue = 0;
for (let layer of AP.layers) {
   if (layer.metal) {
      metalValue += layer.value;
   }
}
if (applyAP) {
   args.totalWoundLoss += metalValue;
   let newUsed = AP.used - metalValue;

   let apIndex = args.messageElements.findIndex(i => i.includes(game.i18n.localize("AP")));
   args.messageElements[apIndex] = newUsed + "/" + AP.value + " " + game.i18n.localize("AP");
}

let fortunePoints = {{overcast.usage.other.current}};
let current = this.actor.status.fortune.value;

this.actor.update({"system.status.fortune.value" : fortunePoints + current});

ChatMessage.create({content : `<b>${this.actor.prototypeToken.name}</b> fortune points increased from ${current} to ${fortunePoints + current}`, speaker : {alias : this.effect.label}});

if (this.actor.isOwner) {
    await args.actor.addCondition("fatigued")
}

if (this.actor.isOwner)
{
    await args.actor.addCondition("ablaze")
}

if (this.actor.isOwner) {
    await args.actor.addCondition("fatigued")
}

let value = 1;
let name = this.actor.prototypeToken.name;

if (game.user.isGM) {
  if (game.user.targets.size) {
    game.user.targets.forEach(t => {
      t.actor.applyTerror(value, name)
    });
    game.user.updateTokenTargets([]);
  }
} else {
  game.wfrp4e.utility.postTerror(value, name);
}

if (this.actor.isOwner) {
    await args.actor.addCondition("fatigued")
}

let applyAP = (args.damageType == game.wfrp4e.config.DAMAGE_TYPE.IGNORE_TB || args.damageType == game.wfrp4e.config.DAMAGE_TYPE.NORMAL);
let AP = args.AP;
let metalValue = 0;
for (let layer of AP.layers) {
   if (layer.metal) {
      metalValue += layer.value
   }
}
if (metalValue) {
   args.messageElements.push(`-${metalValue} Metal Armour`);
}
args.totalWoundLoss += metalValue;
if (applyAP) {
   args.totalWoundLoss += metalValue;
   let newUsed = AP.used - metalValue;
   let apIndex = args.messageElements.findIndex(i => i.includes(game.i18n.localize("AP")));
   args.messageElements[apIndex] = newUsed + "/" + AP.value + " " + game.i18n.localize("AP");
}

if (this.actor.isOwner) {
    await args.actor.addCondition("fatigued")
}

let caster = await fromUuid(this.effect.origin);
if (this.actor.isOwner) {
    if (!this.actor.has(game.i18n.localize("NAME.Daemonic")) && !this.actor.has(game.i18n.localize("NAME.Undead"))) {
        let bleeding = this.actor.hasCondition("bleeding");
        let fatigued = this.actor.hasCondition("fatigued");
        if (bleeding) await this.actor.removeCondition("bleeding", bleeding.flags.wfrp4e.value);
        if (fatigued) await this.actor.removeCondition("fatigued", fatigued.flags.wfrp4e.value);
    } else if (this.actor.has(game.i18n.localize("NAME.Undead"))) {
        await this.actor.applyBasicDamage(caster.characteristics.wp.bonus, {damageType : game.wfrp4e.config.DAMAGE_TYPE.IGNORE_ALL});
    }
}

let applyAP = (args.damageType == game.wfrp4e.config.DAMAGE_TYPE.IGNORE_TB || args.damageType == game.wfrp4e.config.DAMAGE_TYPE.NORMAL);
if (applyAP) {
   let AP = args.AP;

   args.totalWoundLoss += AP.used;
   let apIndex = args.messageElements.findIndex(i => i.includes(game.i18n.localize("AP")));
