oAsvKkgS9pLtavj7.script:
if (args.test.item && args.test.item.name == game.i18n.localize("NAME.Charm") &&  (args.test.result.roll <= 5 || args.test.result.roll <= args.test.target)) {
   let SL = Math.floor(args.test.target / 10) - Math.floor(args.test.result.roll / 10)
   let ones = Number(args.test.result.roll.toString().split("").pop())
   if (ones > SL) {
     args.test.result.other.push(`<b>${this.effect.label}</b>: ${ones + args.test.successBonus + args.test.slBonus} SL if debating.`);
   }
}

