if (args.type == "characteristic") {
  if (args.options.dodge && this.actor.isMounted) {
    args.prefillModifiers.modifier += 20
  }
}

if (args.type == "skill") {
  if (args.item.name == game.i18n.localize("NAME.Dodge") && this.actor.isMounted) {
    args.prefillModifiers.modifier += 20
  }
}

ScsXdMC7SqNirMwx.script:
if (args.sizeDiff <= -2 && args.opposedTest.attackerTest.result.critical) {
  args.damageMultiplier = Math.abs(args.sizeDiff)
}
let sBonusDiff = args.opposedTest.defenderTest.actor.characteristics.t.bonus - args.opposedTest.attackerTest.actor.characteristics.s.bonus;
let weapon = args.opposedTest.attackerTest.item;
if (sBonusDiff > 0 && weapon && weapon.damage.value.includes("SB")) {
  args.damage += sBonusDiff;
}

yTGSEMqLFPOts08w.script:
args.totalWoundLoss -= 1;
args.messageElements.push("1 " + game.i18n.localize("NAME.Robust"));
if (args.totalWoundLoss <= 0) {
  let weapon = args.opposedTest.attackerTest.weapon;
  if (weapon && weapon.properties.flaws.undamaging) {
    args.totalWoundLoss = 0;
  } else {
    args.totalWoundLoss = 1;
  }
}

FT9ngGg2C68sBxzc.script:
if (args.opposedTest.attackerTest.weapon.attackType == "ranged") {
    let applyAP = (args.damageType == game.wfrp4e.config.DAMAGE_TYPE.IGNORE_TB || args.damageType == game.wfrp4e.config.DAMAGE_TYPE.NORMAL);
    let AP = args.AP;
    if (applyAP) {
        newUsed = Math.max(AP.used - this.item.Advances, 0);
        args.totalWoundLoss += (AP.used - newUsed);
        let apIndex = args.messageElements.findIndex(i => i.includes(game.i18n.localize("AP")))
        args.messageElements[apIndex] = newUsed + "/" + AP.value + " " + game.i18n.localize("AP")
    }
}

7i8RGItRAF5GLESP.script:
let initiativeSetting = game.settings.get("wfrp4e", "initiativeRule");

switch (initiativeSetting) {
  case "default":
    args.initiative += "+10"
    break;

  case "sl":
    args.initiative += "+1"
    break;

  case "d10Init":
    args.initiative += "+10"
    break;

  case "d10InitAgi":
    args.initiative += "+1"
    break;
}
