let wounds = this.actor.status.wounds;
if (wounds.value > 0) {
  ui.notifications.notify(game.i18n.format("WFRP4E.EFFECT.CORE.HealingPotion", {wounds: this.actor.characteristics.t.bonus}));
  this.actor.modifyWounds(this.actor.characteristics.t.bonus);
} else {
  ui.notifications.error(game.i18n.localize("WFRP4E.EFFECT.CORE.HealingPotionNoWounds"));
}

